                    <node TEXT="目录中只需要记录文件的其实磁盘块号" ID="LkRV9iwZSx" _mubu_text="%3Cspan%3E%E7%9B%AE%E5%BD%95%E4%B8%AD%E5%8F%AA%E9%9C%80%E8%A6%81%E8%AE%B0%E5%BD%95%E6%96%87%E4%BB%B6%E7%9A%84%E5%85%B6%E5%AE%9E%E7%A3%81%E7%9B%98%E5%9D%97%E5%8F%B7%3C/span%3E" STYLE="fork"/>
