                <node TEXT="索引分配" ID="ClrJbSJNmL" _mubu_text="%3Cspan%3E%E7%B4%A2%E5%BC%95%E5%88%86%E9%85%8D%3C/span%3E" STYLE="fork">
                  <node TEXT="索引分配允许文件离散地分配在各个磁盘块中，系统会为每个文件创建一张索引表，索引表中会记录文件的各个逻辑块对应的物理块（索引表的功能类似于内存管理中的页表——建立逻辑页面和物理页面之间的映射关系）" ID="PwcuFUENhi" _mubu_text="%3Cspan%3E%E7%B4%A2%E5%BC%95%E5%88%86%E9%85%8D%E5%85%81%E8%AE%B8%E6%96%87%E4%BB%B6%E7%A6%BB%E6%95%A3%E5%9C%B0%E5%88%86%E9%85%8D%E5%9C%A8%E5%90%84%E4%B8%AA%E7%A3%81%E7%9B%98%E5%9D%97%E4%B8%AD%EF%BC%8C%E7%B3%BB%E7%BB%9F%E4%BC%9A%E4%B8%BA%E6%AF%8F%E4%B8%AA%E6%96%87%E4%BB%B6%E5%88%9B%E5%BB%BA%E4%B8%80%E5%BC%A0%E7%B4%A2%E5%BC%95%E8%A1%A8%EF%BC%8C%E7%B4%A2%E5%BC%95%E8%A1%A8%E4%B8%AD%E4%BC%9A%E8%AE%B0%E5%BD%95%E6%96%87%E4%BB%B6%E7%9A%84%E5%90%84%E4%B8%AA%E9%80%BB%E8%BE%91%E5%9D%97%E5%AF%B9%E5%BA%94%E7%9A%84%E7%89%A9%E7%90%86%E5%9D%97%EF%BC%88%E7%B4%A2%E5%BC%95%E8%A1%A8%E7%9A%84%E5%8A%9F%E8%83%BD%E7%B1%BB%E4%BC%BC%E4%BA%8E%E5%86%85%E5%AD%98%E7%AE%A1%E7%90%86%E4%B8%AD%E7%9A%84%E9%A1%B5%E8%A1%A8%E2%80%94%E2%80%94%E5%BB%BA%E7%AB%8B%E9%80%BB%E8%BE%91%E9%A1%B5%E9%9D%A2%E5%92%8C%E7%89%A9%E7%90%86%E9%A1%B5%E9%9D%A2%E4%B9%8B%E9%97%B4%E7%9A%84%E6%98%A0%E5%B0%84%E5%85%B3%E7%B3%BB%EF%BC%89%3C/span%3E" STYLE="fork"/>
                  <node TEXT="索引表存放的磁盘块称为索引快" ID="zNLfWuJhuG" _mubu_text="%3Cspan%3E%E7%B4%A2%E5%BC%95%E8%A1%A8%E5%AD%98%E6%94%BE%E7%9A%84%E7%A3%81%E7%9B%98%E5%9D%97%E7%A7%B0%E4%B8%BA%E7%B4%A2%E5%BC%95%E5%BF%AB%3C/span%3E" STYLE="fork"/>
